import React, { Component } from 'react';
import './App.css';
import InputBar from './InputBar';
import ColorsList from './ColorsList';
import axios from 'axios';

const Clarifai = require('clarifai');

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      //intialize it with empty array or will trigger error at first render
      colors: [],
      theColorApiRes: '',
      imgSRC: 'https://mymodernmet.com/wp/wp-content/uploads/archive/ocgy1ixCGE6d0SzzinRj_1082098615.jpeg',
      input: '',
      hexSelected: '',
      errorMsg: ''
    };
  }

  componentDidMount() {
    let url = this.state.imgSRC;
    this.clariaApiCall(url);
  }

  clariaApiCall (url) {
    const app = new Clarifai.App({apiKey: 'bd8644854b19417dacdfa3adba21aab1'});
    app.models.predict("eeed0b6733a644cea07cf4c60f87ebb7", url)
    .then(res => {
      this.setState({ colors: res.outputs[0].data.colors });
    },
      err => {
        console.log(err);
        this.setState({errorMsg: err});
      }
    );
  }

  colorApiCall (event) {
    let hex = event.target.dataset.id;
    hex = hex.slice(1, hex.length)
    console.log(hex);
    axios.get(`http://www.thecolorapi.com/id?hex=${hex}`)
    .then(res => {
      console.log(res);
      this.setState({theColorApiRes: res})
    },
    err => {
       console.log(err.message);
       this.setState({errorMessage: err.message});
     })
  }

  imgSearch(event) {
    this.setState({input: event.target.value});
  }

  searchButtonClick() {
    let url = this.state.input;
    this.setState({
      imgSRC: url
    },
      this.clariaApiCall(url)
    );
  }

  colorInfoRender() {
    if (this.state.theColorApiRes = '') {
      return (<div></div>);
    } else {
      return(
        <div>
          <p>{this.state.theColorApiRes.data.cmyk}</p>
          <p>{this.state.theColorApiRes.data.rgb}</p>
        </div>
      );
    }
  }

  render() {
    return (
      <div className="App">
        <h2>Choose a picture and analyze it</h2>
        <img
          className="sample-img"
          alt="stairs"
          src={this.state.imgSRC} />
          <InputBar
          searchButtonClick={event => this.searchButtonClick(event)}
          imgSearch={event => this.imgSearch(event)}/>
          <ColorsList
            colorApiCall={event => this.colorApiCall(event)}
            colors={this.state.colors}
          />

      </div>
    );
  }
}

export default App;
