import React, { Component } from 'react';
import './App.css';
const Clarifai = require('clarifai');

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      //intialize it with empty array or will trigger error
      colors: []
    };
  }

  componentDidMount() {
    const app = new Clarifai.App({apiKey: 'bd8644854b19417dacdfa3adba21aab1'});
    app.models.predict("eeed0b6733a644cea07cf4c60f87ebb7", "https://mymodernmet.com/wp/wp-content/uploads/archive/ocgy1ixCGE6d0SzzinRj_1082098615.jpeg")
    .then(res => {
      this.setState({ colors: res.outputs[0].data.colors });
    },
      err => {
        console.log(err);
      }
    );
  }

  render() {
    console.log(this.state.colors);

    const ColorsList = this.state.colors.map(color =>
      <div>
        <div
          className="color-square"
          style={{"backgroundColor": color.raw_hex}}>
        </div>
        <p>{color.w3c.name}</p>
      </div>
    );

    return (
      <div className="App">
        {ColorsList}
      </div>
    );
  }
}

export default App;
