import  React from 'react';
import { connect } from 'react-redux';
import { changeMainImage } from '../actions';

const ImageSelection = ({url}) => {
  // console.log(url)
  const Images = url.map(url =>
    <img
      key={url}
      className="image-selection"
      alt="selection"
      src={url}
      onClick={this.props.changeMainImage(url)}
    />
  );

  return(
    <div>
      {Images}
    </div>
  );
};

const mapStateToProps = state => {
  return {
    url: state.url.exampleImages
  };
};

export default connect(mapStateToProps, { changeMainImage })(ImageSelection);
