import React, { Component } from 'react';
import InputBar from './InputBar';
import './App.css';
const Clarifai = require('clarifai');

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      //intialize it with empty array or will trigger error at first render
      colors: [],
      imgSRC: 'https://mymodernmet.com/wp/wp-content/uploads/archive/ocgy1ixCGE6d0SzzinRj_1082098615.jpeg',
      input: '',
      errorMsg: ''
    };
  }

  componentDidMount() {
    let url = this.state.imgSRC;
    this.ApiCall(url);
  }
  // change return numberes to percent
  toPercent(num) {
    return Math.floor(num*100) + '%';
  }

  ApiCall (url) {
    const app = new Clarifai.App({apiKey: 'bd8644854b19417dacdfa3adba21aab1'});
    app.models.predict("eeed0b6733a644cea07cf4c60f87ebb7", url)
    .then(res => {
      this.setState({ colors: res.outputs[0].data.colors });
    },
      err => {
        console.log(err);
        this.setState({errorMsg: err});
      }
    );
  }

  imgSearch(event) {
    this.setState({input: event.target.value});
  }

  searchButtonClick() {
    let url = this.state.input;
    this.setState({
      imgSRC: url
    },
      this.ApiCall(url)
    );
  }

  render() {
    let sortColors = this.state.colors.sort((a,b) => b.value - a.value);
    console.log(sortColors);

    const ColorsList = sortColors.map(color =>
      <div
        key={color.raw_hex}
        className="color-wrapper">
        <div
          className="color-square"
          style={{"backgroundColor": color.raw_hex}}>
        </div>
        <p>{color.w3c.name}</p>
        <p>{this.toPercent(color.value)}</p>
      </div>
    );

    return (
      <div className="App">
        <h2>Choose a picture and analyze it</h2>
        <img
          className="sample-img"
          alt="stairs"
          src={this.state.imgSRC} />
          <InputBar
          searchButtonClick={event => this.searchButtonClick(event)}
          imgSearch={event => this.imgSearch(event)}/>
          {ColorsList}
      </div>
    );
  }
}

export default App;
