import React, { Component } from 'react';
import { connect } from 'react-redux';
import { changeMainImage } from '../actions';

class ImageSelection extends Component {
  changeImage(url) {
    this.props.changeMainImage(url);
  }

  render() {
    // console.log(url)
    const Images = this.props.url.map(url =>
      <div
        key={url}
        onClick={this.changeImage(url)}
      >
        <img
          className="image-selection"
          alt="selection"
          src={url}
        />
      </div>

    );

    return(
      <div>
        {Images}
      </div>
    );
  }
};

const mapStateToProps = state => {
  return {
    url: state.url.exampleImages
  };
};

export default connect(mapStateToProps, { changeMainImage })(ImageSelection);
